net.rptools.maptool.model.MacroButtonPro.command:
[h: pool = arg(0)]
[h: pool = listSort(pool, "N-")]
[r, if(listCount(pool)<1), code: {0};{
	[h: die1 = listGet(pool, 0)]
	[r, if(listCount(pool)>=2), code: {
		[h: die2 = listGet(pool,1)]
		[r: listGet(die1, 0, "/") + listGet(die2, 0, "/")]
	};{
		[r:listGet(die1, 0, "/")]
	}]
}]

[h: id = arg(0)]
[h: name = getName(id)+"s pool"]
[frame(name): { 
	[r:frameContent(id, name)]
}]

[h: pcs = getTokens(",","{pc:1}")]
[h, foreach( id, pcs ), code: {
[h, macro("addTokenToInitiative@Lib:DitV"): id]
}]

.odd { background-color: #FFFFFF }
.even { background-color: #EEEEAA }
th { background-color: #113311; color: #FFFFFF }

[h: id = arg(0)]
[h: name = "Edit "+getName(id)]
[frame(name): { 
	[r, macro("csEditor@Lib:DitV"):id]
}]

[h: assert(isGM(),"GM only!")]
[h: status=input("dummy|Will delete your Character! Sure?||LABEL|SPAN=TRUE")]
[h: abort(status)]
[h, foreach( id, getSelected()), code: {
[h, macro("setupToken@Lib:DitV"):id]
}]

[r, foreach(id, getSelected(), "<br>"), code:{
[h: pool = getProperty("myPool", id)]
[h: status = input("pool|"+pool+"|"+getName(id)+"s pool|TEXT")]
[h: abort(status)]
[h: setProperty("myPool", pool, id)]
}]
